import React, { useState, useEffect, useRef } from "react";
import classNames from "classnames";

export function InputSelect<TItem>({
  label,
  defaultValue,
  onChange: consumerOnChange,
  items,
  parseItem,
  isLoading,
  loadingLabel,
}) {
  const [selectedValue, setSelectedValue] = useState<TItem | null>(defaultValue ?? null);
  const [isOpen, setIsOpen] = useState(false);
  const [highlightedIndex, setHighlightedIndex] = useState(-1);
  const dropdownRef = useRef<HTMLDivElement | null>(null);
  const inputRef = useRef<HTMLDivElement | null>(null);

  const toggleDropdown = () => setIsOpen((prev) => !prev);

  const handleSelect = (item: TItem) => {
    setSelectedValue(item);
    consumerOnChange(item);
    setIsOpen(false);
    setHighlightedIndex(-1); // Reset highlighted index
  };

  const handleKeyDown = (event: React.KeyboardEvent) => {
    if (event.key === "Enter" || event.key === " ") {
      toggleDropdown();
    } else if (event.key === "ArrowDown") {
      setHighlightedIndex((prev) => Math.min(prev + 1, items.length - 1));
    } else if (event.key === "ArrowUp") {
      setHighlightedIndex((prev) => Math.max(prev - 1, -1));
    } else if (event.key === "Escape") {
      setIsOpen(false);
    }
  };

  const handleClickOutside = (event: MouseEvent) => {
    if (
      dropdownRef.current &&
      !dropdownRef.current.contains(event.target as Node) &&
      inputRef.current &&
      !inputRef.current.contains(event.target as Node)
    ) {
      setIsOpen(false);
    }
  };

  useEffect(() => {
    document.addEventListener("mousedown", handleClickOutside);
    return () => {
      document.removeEventListener("mousedown", handleClickOutside);
    };
  }, []);

  return (
    <div className="RampInputSelect--root" ref={dropdownRef}>
      <label className="RampText--s RampText--hushed">{label}</label>
      <div className="RampBreak--xs" />
      <div
        className="RampInputSelect--input"
        ref={inputRef}
        onClick={toggleDropdown}
        onKeyDown={handleKeyDown}
        role="button"
        tabIndex={0} // Make it focusable
      >
        {selectedValue ? parseItem(selectedValue).label : "Select an option"}
      </div>

      {isOpen && (
        <div
          className="RampInputSelect--dropdown-container"
          style={{
            position: "relative", // Ensure it's positioned relative to the input
          }}
        >
          {isLoading ? (
            <div className="RampInputSelect--dropdown-item">{loadingLabel}...</div>
          ) : items.length === 0 ? (
            <div className="RampInputSelect--dropdown-item">No items</div>
          ) : (
            items.map((item, index) => {
              const parsedItem = parseItem(item);
              return (
                <div
                  key={parsedItem.value}
                  className={classNames("RampInputSelect--dropdown-item", {
                    "RampInputSelect--dropdown-item-highlighted": highlightedIndex === index,
                  })}
                  onMouseEnter={() => setHighlightedIndex(index)}
                  onClick={() => handleSelect(item)}
                >
                  {parsedItem.label}
                </div>
              );
            })
          )}
        </div>
      )}
    </div>
  );
}
